mapper.select:
SELECT NAME, AD_NO, ADC_NO, ID, AD_CONTENT, AD_LIKE, AD_LIKE_ID, TO_CHAR(AL_GRADE,'0.0') AS AL_GRADE, PROFILE_IMAGE, ROWNUM RNUM FROM( SELECT M.NAME, AC.AD_NO, AC.ADC_NO, AC.ID, AC.AD_CONTENT, AC.AD_LIKE, AC.AD_LIKE_ID, AL.AL_GRADE, PROFILE_IMAGE FROM AD_LIKE AL, AD_COMMENT AC ,MEMBER M WHERE AL.AD_LIKE_NO=AC.AD_NO AND AL.ID=AC.ID AND AL.ID=M.ID AND AC.AD_NO=#{AD_NO}
ORDER BY AD_REG_DATE DESC
ORDER BY AD_LIKE DESC
)ORDER BY RNUM ASC

mapper.update:
update AD_COMMENT
	set AD_LIKE = #{AD_LIKE}, AD_LIKE_ID=#{AD_LIKE_ID}
where AD_NO=#{AD_NO} AND ADC_NO=#{ADC_NO}
