mapper.update:
UPDATE
	member 
SET
	profile_image=#{profile_image, jdbcType=VARCHAR} 
WHERE
	id=#{id, jdbcType=VARCHAR}

UPDATE member SET
profile_image=#{profile_image},
password=#{password}, name=#{name}, tel=#{tel}, admin=to_number(#{admin}), email_marketing=to_number(#{email_marketing}), sms_marketing=to_number(#{sms_marketing}) WHERE id=#{id}

UPDATE DRAMA SET DRAMA_NAME=#{name}, DRAMA_DATE=TO_DATE(#{date}), DRAMA_CHANNEL=#{channel}, DRAMA_GENRE=#{genre}, DRAMA_DIRECTOR=#{director}, DRAMA_CONTENT=#{content}, DRAMA_EPISODE=#{episode}
, DRAMA_MAIN_IMAGE=#{main_image}, DRAMA_POSTER_IMAGE=#{poster_image}, DRAMA_CONTENT_IMAGE=#{content_image}
WHERE DRAMA_NO=#{no}

UPDATE MOVIE SET MOVIE_NAME=#{name}, MOVIE_DATE=TO_DATE(#{date}), MOVIE_COUNTRY=#{country}, MOVIE_GENRE=#{genre}, MOVIE_TOTAL_COUNT=#{total_count}, MOVIE_DIRECTOR=#{director}, MOVIE_CONTENT=#{content}, MOVIE_RUNNINGTIME=#{runningtime}
, MOVIE_MAIN_IMAGE=#{main_image}, MOVIE_POSTER_IMAGE=#{poster_image}, MOVIE_GALLERY_IMAGE=#{content_image}
WHERE MOVIE_NO=#{no}

UPDATE AD SET AD_NAME=#{name}, AD_LINK=#{link}, AD_CONTENT=#{content}, AD_COMPANY=#{company}
, AD_MAIN_IMAGE=#{main_image}, AD_POSTER_IMAGE=#{poster_image}, AD_CONTENT_IMAGE=#{content_image}
WHERE AD_NO=#{no}
