mapper.select:
SELECT DRAMA_NO, DC_NO, ID, DC_REG_DATE, DC_CONTENT, DC_LIKE, DC_LIKE_ID, TO_CHAR(DL_GRADE,'0.0') AS DL_GRADE, PROFILE_IMAGE, ROWNUM RNUM FROM( SELECT DC.DRAMA_NO, DC.DC_NO, DC.ID, DC.DC_REG_DATE, DC.DC_CONTENT, DC.DC_LIKE, DC.DC_LIKE_ID, DL.DL_GRADE, PROFILE_IMAGE FROM DRAMA_LIKE DL, DRAMA_COMMENT DC ,MEMBER M WHERE DL.DRAMA_LIKE_NO=DC.DRAMA_NO AND DL.ID=DC.ID and DL.ID=M.ID AND DC.DRAMA_NO=#{DRAMA_NO}
ORDER BY DC_REG_DATE DESC
ORDER BY DC_LIKE DESC
)ORDER BY RNUM ASC

mapper.update:
update DRAMA_COMMENT
	set DC_LIKE = #{DC_LIKE}, DC_LIKE_ID=#{DC_LIKE_ID}
where DRAMA_NO=#{DRAMA_NO} AND DC_NO=#{DC_NO}
